public static String getCgroupsRelativeRoot() {
  if (cGroupsHandler == null) {
    return null;
  }
  String cGroupPath = cGroupsHandler.getRelativePathForCGroup("");
  if (cGroupPath == null || cGroupPath.isEmpty()) {
    return null;
  }
  return cGroupPath.replaceAll("/$", "");
}
