public BatchBuilder addContainerClass(int classId, int rateMbit, boolean
    strictMode) {
  int ceilMbit;

  if (strictMode) {
    ceilMbit = rateMbit;
  } else {
    ceilMbit = yarnBandwidthMbit;
  }

  commands.add(getStringForAddContainerClass(classId, rateMbit, ceilMbit));
  return this;
}
