public synchronized void cleanupAssignGpus(ContainerId containerId) {
  Iterator<Map.Entry<Integer, ContainerId>> iter =
      usedDevices.entrySet().iterator();
  while (iter.hasNext()) {
    if (iter.next().getValue().equals(containerId)) {
      iter.remove();
    }
  }
}
