  try {
    synchronized (serviceMap) {
      for (Service service : serviceMap.values()) {
        if (service.getServiceState() == Service.STATE.STARTED) {
          service.unregister(this);
          service.stop();
        }
      }
      serviceMap.clear();
    }
  } finally {
    super.stop();
  }
}
