boolean checkLocalResource(LocalizedResource rsrc) {
  List<String> localDirs = dirsHandler.getLocalDirsForRead();
  for (String dir : localDirs) {
    if (isParent(rsrc.getLocalPath().toUri().getPath(), dir)) {
      return true;
    } else {
      continue;
    }
  }
  return false;
}
