void incDelSize(String user, long delSize) {
  totalDelSize += delSize;
  if (user == null) {
    publicDelSize += delSize;
  } else {
    privateDelSize += delSize;
    Long userDel = userDelSizes.get(user);
    if (userDel != null) {
      userDel += delSize;
      userDelSizes.put(user, userDel);
    } else {
      userDelSizes.put(user, delSize);
    }
  }
}
