public void endContainerLocalization(String locId) {
  LocalizerRunner localizer;
  synchronized (privLocalizers) {
    localizer = privLocalizers.get(locId);
    if (null == localizer) {
      return; // ignore
    }
  }
  localizer.endContainerLocalization();
}
