    throws IOException {
  if (destDirPath == null || destDirPath.equals(destDirRoot)) {
    return;
  }
  createParentDirs(destDirPath.getParent(), destDirRoot);
  createDir(destDirPath, PUBLIC_FILECACHE_FOLDER_PERMS);
}
