private void cleanupLogDirs(FileContext fs, DeletionService del) {
  for (String logDir : dirsHandler.getLogDirsForCleanup()) {
    try {
      cleanupLogDir(fs, del, logDir);
    } catch (IOException e) {
      LOG.warn("failed to cleanup app log dir " + logDir, e);
    }
  }
}
