public LocalResource getResource() {
  ResourceLocalizationSpecProtoOrBuilder p = viaProto ? proto : builder;
  if (resource != null) {
    return resource;
  }
  if (!p.hasResource()) {
    return null;
  }
  resource = new LocalResourcePBImpl(p.getResource());
  return resource;
}
