private static int getConfiguredVCores(Configuration conf) {
  int cores = conf.getInt(YarnConfiguration.NM_VCORES,
      YarnConfiguration.DEFAULT_NM_VCORES);
  if (cores == -1) {
    cores = YarnConfiguration.DEFAULT_NM_VCORES;
  }
  return cores;
}
