        (int) (Runtime.getRuntime().maxMemory() / (1024 * 1024));
    int containerPhysicalMemoryMB =
        (int) (0.8f * (physicalMemoryMB - (2 * hadoopHeapSizeMB)));
    int reservedMemoryMB =
        conf.getInt(YarnConfiguration.NM_SYSTEM_RESERVED_PMEM_MB, -1);
