  tEvent.setTimestamp(createdTime);
  entity.addEvent(tEvent);

  getDispatcher().getEventHandler().handle(new TimelineV2PublishEvent(
      SystemMetricsEventType.PUBLISH_ENTITY, entity, app.getApplicationId()));
}

