private boolean updateBlockLocationsStamp() throws IOException {
  if (!isLocatedBlocksExpired()) {
    return false;
  }
  // clear dead nodes
  deadNodes.clear();
  openInfo(true);
  setLocatedBlocksTimeStamp();
  return true;
}
