public void readFully(long position, final ByteBuffer buf)
    throws IOException {
  int nread = 0;
  while (buf.hasRemaining()) {
    int nbytes = read(position + nread, buf);
    if (nbytes < 0) {
      throw new EOFException(FSExceptionMessages.EOF_IN_READ_FULLY);
    }
    nread += nbytes;
  }
}
