          div(_PROGRESSBAR).
            $title(join(percent, '%')). // tooltip
            div(_PROGRESSBAR_VALUE).
              $style(join("width:", percent, '%'))._()._()._().
        td().
          a(!appInfo.isTrackingUrlReady()?
            "#" : appInfo.getTrackingUrlPretty(), appInfo.getTrackingUI())._()._();
  }
  tbody._()._();
}

