public Resource getCapacityAtTime(long tick) {
  readLock.lock();
  try {
    Entry<Long, Resource> closestStep = cumulativeCapacity.floorEntry(tick);
    if (closestStep != null) {
      return Resources.clone(closestStep.getValue());
    }
    return Resources.clone(ZERO_RESOURCE);
  } finally {
    readLock.unlock();
  }
}
