public void reinitialize(Configuration conf, RMContext rmContext)
    throws YarnException {
  writeLock.lock();
  try {
    if (!initialized) {
      initialize(conf);
      initialized = true;
    } else {
      initializeNewPlans(conf);
    }
  } finally {
    writeLock.unlock();
  }
}
