public static RMPolicyProvider getInstance() {
  if (rmPolicyProvider == null) {
    synchronized(RMPolicyProvider.class) {
      if (rmPolicyProvider == null) {
        rmPolicyProvider = new RMPolicyProvider();
      }
    }
  }
  return rmPolicyProvider;
}
