      conf.getTimeDuration(YarnConfiguration.RM_DT_RENEWER_THREAD_TIMEOUT,
          YarnConfiguration.DEFAULT_RM_DT_RENEWER_THREAD_TIMEOUT,
          TimeUnit.MILLISECONDS);
