public String getExplanation() {
  StringBuilder sb = new StringBuilder();

  if (message != null) {
    sb.append(message);

    if (cause != null) {
      sb.append(": ");
    }
  }

  if (cause != null) {
    sb.append(StringUtils.stringifyException(cause));
  }

  return sb.toString();
}
