      if (mapping.getQueue().equals(CURRENT_APP_MAPPING)) {
        return getPlacementContext(mapping, String.valueOf(applicationId));
      } else {
        return getPlacementContext(mapping);
      }
    }
    if (mapping.getSource().equals(applicationId.toString())) {
      return getPlacementContext(mapping);
    }
  }
  return null;
}
