public Resource getAllocatedOrReservedResource() {
  try {
    readLock.lock();
    if (getState().equals(RMContainerState.RESERVED)) {
      return getReservedResource();
    } else {
      return getAllocatedResource();
    }
  } finally {
    readLock.unlock();
  }
}
