public int getActiveNMCountPerLabel(String label) {
  if (label == null) {
    return 0;
  }
  try {
    readLock.lock();
    RMNodeLabel labelInfo = labelCollections.get(label);
    return (labelInfo == null) ? 0 : labelInfo.getNumActiveNMs();
  } finally {
    readLock.unlock();
  }
}
