public void deactivateNode(NodeId nodeId) {
  try {
    writeLock.lock();
    Host host = nodeCollections.get(nodeId.getHost());
    for (NodeAttribute attribute : host.getAttributes().keySet()) {
      clusterAttributes.get(attribute).removeNode(host.getResource());
    }
    host.deactivateNode();
  } finally {
    writeLock.unlock();
  }
}
