public NodeId getRecordingNodeId(SchedulerNode node) {
  if (node != null) {
    return node.getNodeID();
  } else if (isRecordingMultiNodes()) {
    return ActivitiesManager.EMPTY_NODE_ID;
  }
  return null;
}
