public boolean updateSchedulingRequests(
    List<SchedulingRequest> requests) {
  if (requests == null) {
    return false;
  }

  try {
    writeLock.lock();
    if (!isStopped) {
      return appSchedulingInfo.updateSchedulingRequests(requests, false);
    }
    return false;
  } finally {
    writeLock.unlock();
  }
}
