private void updateNMToken(Container container) {
  NMToken nmToken =
      rmContext.getNMTokenSecretManager().createAndGetNMToken(getUser(),
          getApplicationAttemptId(), container);
  if (nmToken != null) {
    updatedNMTokens.add(nmToken);
  }
}
