private void updateAttributesOnNode(List<NodeId> nodeIds,
    Set<NodeAttribute> attributes) {
  nodeIds.forEach((k) -> {
    SchedulerNode node = nodeTracker.getNode(k);
    node.updateNodeAttributes(attributes);
  });
