      if (LOG.isDebugEnabled()) {
        LOG.debug(
            "Failed to move reservation, node updated or removed, moving "
                + "cancelled.");
      }
      return false;
    }

    // Target node's reservation status changed?
    if (targetNode.getReservedContainer() != null) {
      if (LOG.isDebugEnabled()) {
        LOG.debug(
