public void stopQueue() {
  try {
    writeLock.lock();
    if (getNumApplications() > 0) {
      updateQueueState(QueueState.DRAINING);
    } else {
      updateQueueState(QueueState.STOPPED);
    }
  } finally {
    writeLock.unlock();
  }
}
