private void applyMutation(Configuration conf, Map<String, String> kvUpdate) {
  for (Map.Entry<String, String> kv : kvUpdate.entrySet()) {
    if (kv.getValue() == null) {
      conf.unset(kv.getKey());
    } else {
      conf.set(kv.getKey(), kv.getValue());
    }
  }
}
