public void setAccessibleNodeLabels(String queue, Set<String> labels) {
  if (labels == null) {
    return;
  }
  String str = StringUtils.join(",", labels);
  set(getQueuePrefix(queue) + ACCESSIBLE_NODE_LABELS, str);
}
