    new HashMap<QueueACL, AccessControlList>();
  String queuePrefix = getQueuePrefix(queue);
  for (QueueACL acl : QueueACL.values()) {
    acls.put(acl, 
        new AccessControlList(get(queuePrefix + getAclKey(acl), 
            DEFAULT_ACL)));
  }
  return acls;
}
