public int getOutstandingAsksCount(String resourceName) {
  readLock.lock();
  try {
    if (resourceName.equals("*") && schedulingRequest != null) {
      return schedulingRequest.getResourceSizing().getNumAllocations();
    }
    return 0;
  } finally {
    readLock.unlock();
  }
}
