public Resource getCachedDemand(String label) {
  try {
    readLock.lock();
    Resource demand = Resources.createResource(0);
    Resources.addTo(demand, getCachedUsed(label));
    Resources.addTo(demand, getCachedPending(label));
    return demand;
  } finally {
    readLock.unlock();
  }
}
