public Resource getMaximumContainerAllocation() {
  if (getName().equals("root")) {
    return maxContainerAllocation;
  }
  if (maxContainerAllocation.equals(Resources.unbounded())
      && getParent() != null) {
    return getParent().getMaximumContainerAllocation();
  } else {
    return maxContainerAllocation;
  }
}
