void sortRatios(float[][] ratios1, float[][]ratios2) {
  // sort order descending by resource share
  Arrays.sort(ratios1, (float[] o1, float[] o2) ->
      (int) Math.signum(o2[0] - o1[0]));
  Arrays.sort(ratios2, (float[] o1, float[] o2) ->
      (int) Math.signum(o2[0] - o1[0]));
}
