int getNumReservations(String rackName, boolean isAny) {
  int counter = 0;
  if (isAny) {
    for (Set<String> nodes : reservations.values()) {
      if (nodes != null) {
        counter += nodes.size();
      }
    }
  } else {
    Set<String> nodes = reservations.get(
            rackName == null ? "NULL" : rackName);
    if (nodes != null) {
      counter += nodes.size();
    }
  }
  return counter;
}
