public void collectSchedulerApplications(
    Collection<ApplicationAttemptId> apps) {
  readLock.lock();
  try {
    for (FSAppAttempt appSched : runnableApps) {
      apps.add(appSched.getApplicationAttemptId());
    }
    for (FSAppAttempt appSched : nonRunnableApps) {
      apps.add(appSched.getApplicationAttemptId());
    }
  } finally {
    readLock.unlock();
  }
}

