public int getNumActiveApps() {
  int numActiveApps = 0;
  readLock.lock();
  try {
    for (FSAppAttempt attempt : runnableApps) {
      if (!attempt.isPending()) {
        numActiveApps++;
      }
    }
  } finally {
    readLock.unlock();
  }
  return numActiveApps;
}
