private List<ApplicationAttemptId> getAppsFromQueue(String queueName)
    throws YarnException {
  List<ApplicationAttemptId> apps = getAppsInQueue(queueName);
  if (apps == null) {
    throw new YarnException("The specified queue: " + queueName
        + " doesn't exist");
  }
  return apps;
}
