public SchedulerNodeReport getNodeReport(NodeId nodeId) {
  readLock.lock();
  try {
    N n = nodes.get(nodeId);
    return n == null ? null : new SchedulerNodeReport(n);
  } finally {
    readLock.unlock();
  }
}
