public Resource getClusterCapacity() {
  readLock.lock();
  try {
    if (staleClusterCapacity == null ||
        !Resources.equals(staleClusterCapacity, clusterCapacity)) {
      staleClusterCapacity = Resources.clone(clusterCapacity);
    }
    return staleClusterCapacity;
  } finally {
    readLock.unlock();
  }
}
