public List<N> getNodes(NodeFilter nodeFilter) {
  List<N> nodeList = new ArrayList<>();
  readLock.lock();
  try {
    if (nodeFilter == null) {
      nodeList.addAll(nodes.values());
    } else {
      for (N node : nodes.values()) {
        if (nodeFilter.accept(node)) {
          nodeList.add(node);
        }
      }
    }
  } finally {
    readLock.unlock();
  }
  return nodeList;
}
