public List<NodeId> getNodeIds(NodeFilter nodeFilter) {
  List<NodeId> nodeList = new ArrayList<>();
  readLock.lock();
  try {
    if (nodeFilter == null) {
      for (N node : nodes.values()) {
        nodeList.add(node.getNodeID());
      }
    } else {
      for (N node : nodes.values()) {
        if (nodeFilter.accept(node)) {
          nodeList.add(node.getNodeID());
        }
      }
    }
  } finally {
    readLock.unlock();
  }
  return nodeList;
}
