public void addSlice(ByteBuffer buffer, int offset, int len) {
  ByteBuffer tmp = buffer.duplicate();
  tmp.position(buffer.position() + offset);
  tmp.limit(buffer.position() + offset + len);
  slices.add(tmp.slice());
}
