public boolean equals(Object obj) {
  if (this == obj)
    return true;
  if (obj == null)
    return false;
  if (getClass() != obj.getClass())
    return false;
  Epoch other = (Epoch) obj;
  if (this.getEpoch() == other.getEpoch()) {
    return true;
  } else {
    return false;
  }
}
