int getNumEntriesInDatabase() throws IOException {
  int numEntries = 0;
  LeveldbIterator iter = null;
  try {
    iter = new LeveldbIterator(db);
    iter.seekToFirst();
    while (iter.hasNext()) {
      Entry<byte[],byte[]> entry = iter.next();
      LOG.info("entry: " + asString(entry.getKey()));
      ++numEntries;
    }
  } catch (DBException e) {
    throw new IOException(e);
  } finally {
    if (iter != null) {
      iter.close();
    }
  }
  return numEntries;
}
