  int completedAttempts = 0;
  // Do not count AM preemption as attempt failure.
  for (RMAppAttempt attempt : attempts.values()) {
    if (!attempt.isPreempted()) {
      completedAttempts++;
    }
  }
  return completedAttempts;
}
